import React, { useState, useEffect, useRef } from "react";
import ReactMarkdown from "react-markdown";
import { User, Bot, Loader2 } from "lucide-react";
import { VoiceInput } from "./VoiceInput";

interface Message {
  id: string;
  text: string;
  sender: "user" | "bot";
  timestamp: Date;
  audio?: string;
}

export const ChatInterface: React.FC = () => {
  // Load messages from sessionStorage or start empty
  const [messages, setMessages] = useState<Message[]>(() => {
    const stored = sessionStorage.getItem("chat-messages");
    if (stored) {
      const parsed = JSON.parse(stored);
      // Restore Date objects
      return parsed.map((msg: any) => ({
        ...msg,
        timestamp: new Date(msg.timestamp),
      }));
    }
    return [];
  });

  const [input, setInput] = useState("");
  const [isThinking, setIsThinking] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement | null>(null);

  // Auto-scroll to bottom on new message
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    sessionStorage.setItem("chat-messages", JSON.stringify(messages));
  }, [messages]);

  // Handle sending a text message
  const handleSend = async () => {
    if (!input.trim()) return;
    const userMsg: Message = {
      id: Date.now() + "-user",
      text: input,
      sender: "user",
      timestamp: new Date(),
    };
    setMessages((prev) => [...prev, userMsg]);
    setInput("");
    setIsThinking(true);

    // Call your backend for bot response (text only)
    try {
      const res = await fetch("/api/text-query", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ question: input }),
      });
      const data = await res.json();
      const botMsg: Message = {
        id: Date.now() + "-bot",
        text: data.answer,
        sender: "bot",
        timestamp: new Date(),
        // audio: data.audio, // If you want TTS for text queries too
      };
      setMessages((prev) => [...prev, botMsg]);
    } catch (err) {
      setMessages((prev) => [
        ...prev,
        {
          id: Date.now() + "-bot",
          text: "Sorry, something went wrong.",
          sender: "bot",
          timestamp: new Date(),
        },
      ]);
    } finally {
      setIsThinking(false);
    }
  };

  // Handle voice input (transcription and bot response)
  const handleVoiceTranscription = (question: string) => {
    setMessages((prev) => [
      ...prev,
      {
        id: Date.now() + "-user",
        text: question,
        sender: "user",
        timestamp: new Date(),
      },
    ]);
    setIsThinking(true);
  };

  const handleBotResponse = (question: string, answer: string, audio: string) => {
    setIsThinking(false);
    setMessages((prev) => [
      ...prev,
      {
        id: Date.now() + "-bot",
        text: answer,
        sender: "bot",
        timestamp: new Date(),
        audio,
      },
    ]);
    // Auto-play audio
    if (audio) {
      const audioObj = new Audio(`data:audio/webm;base64,${audio}`);
      audioObj.play();
    }
  };

  // Handle Enter key for text input
  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="max-h-[70vh] min-h-[24rem] overflow-y-auto p-6 space-y-4 bg-white rounded-xl shadow-lg border border-gray-200 animate-fade-in">
      <div className="text-center mb-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">
          Chat Assistant
        </h2>
        <p className="text-gray-600">
          Ask questions by typing or speaking. Get instant, voice-enabled answers!
        </p>
      </div>
      <div className="space-y-4">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`flex ${
              message.sender === "user" ? "justify-end" : "justify-start"
            } animate-fade-in`}
          >
            <div
              className={`flex items-start space-x-2 max-w-xs lg:max-w-md ${
                message.sender === "user" ? "flex-row-reverse space-x-reverse" : ""
              }`}
            >
              <div
                className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center ${
                  message.sender === "user"
                    ? "bg-blue-600"
                    : "bg-gradient-to-r from-green-500 to-blue-500"
                }`}
              >
                {message.sender === "user" ? (
                  <User className="h-5 w-5 text-white" />
                ) : (
                  <Bot className="h-5 w-5 text-white" />
                )}
              </div>
              <div
                className={`px-4 py-2 rounded-lg ${
                  message.sender === "user"
                    ? "bg-blue-600 text-white text-right"
                    : "bg-gray-100 text-gray-900 text-left"
                }`}
              >
                {message.sender === "bot" ? (
                  <div className="text-sm text-left">
                    <ReactMarkdown>{message.text}</ReactMarkdown>
                    {message.audio && (
                      <button
                        className="mt-2 text-xs text-blue-600 hover:underline"
                        onClick={() => {
                          const audio = new Audio(
                            `data:audio/webm;base64,${message.audio}`
                          );
                          audio.play();
                        }}
                      >
                        ▶️ Play Response
                      </button>
                    )}
                  </div>
                ) : (
                  <p className="text-sm text-left">{message.text}</p>
                )}
                <p className="text-xs mt-1 opacity-70 text-left">
                  {message.timestamp.toLocaleTimeString()}
                </p>
              </div>
            </div>
          </div>
        ))}
        {isThinking && (
          <div className="flex justify-start animate-fade-in">
            <div className="flex items-start space-x-2">
              <div className="flex-shrink-0 w-8 h-8 rounded-full bg-gradient-to-r from-green-500 to-blue-500 flex items-center justify-center">
                <Bot className="h-5 w-5 text-white" />
              </div>
              <div className="bg-gray-100 px-4 py-2 rounded-lg">
                <div className="flex items-center space-x-2">
                  <Loader2 className="h-4 w-4 animate-spin" />
                  <span className="text-sm text-gray-600">Thinking...</span>
                </div>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      {/* Input area */}
      <div className="flex items-center gap-2 mt-4">
        <VoiceInput
          onTranscription={handleVoiceTranscription}
          onBotResponse={handleBotResponse}
        />
        <textarea
          className="flex-1 border rounded-lg p-2"
          placeholder="Type your question and press Enter..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          rows={1}
        />
        <button
          onClick={handleSend}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition"
        >
          Send
        </button>
      </div>
    </div>
  );
};
